create table wfw_comm_queue (                         --  table for queued communications, triggered at EOD
            wfw_comm_queue_id numeric (10) not null,
	    wfw_name varchar (100),
	    wfw_description varchar (500),
            cont_where_clause varchar (1000) not null,  -- where-clause to get the list of Contacts that this instruction's email,SMS must be sent to
            start_send_from timestamp not null,  -- the date-time to start sending the communication from
            end_send_after timestamp,  -- the date-time for the last time this communication is to be sent
            comm_type varchar (10) not null,  -- To send "Email" or "SMS" or something else in future
            template_type varchar (10) not null,  -- Whether "Email" or "SMS" or "URL"
            email_templt numeric (10), -- email_templt_id if template_type is "Email"
            mesg_format numeric (10), -- mesg_format if template_type is "SMS"
            template_from_url varchar (500), -- if template_type is "URL", the URL to hit to get pre-expanded template
            actvt_data_srce_to_set varchar (25) not null,  -- string to write into activity.data_source
            primary_entity numeric (10) not null, -- the  entity that this communication is primarily about - fkey to entitylist
            entt_where_clause varchar (1000), -- the WHERE clause to use on the primary entity to get details for template-expansion
            comm_freq varchar (25) not null, -- frequency of communication - Sun,Mon,Tue,Wed,Thu,Fri,Sat,1st-of-Mth,15th-of-Mth,Every-Day,'' for one-time
            last_exec_start_time timestamp,  -- start-time of last time this rec's instructions were executed
	    attachment_type varchar (10) default 'Inline',    -- "Inline", "PDF" or "XLS"
            created_by numeric (10) not null,
            modified_by numeric (10),
            created_date timestamp not null,
            modified_date timestamp,
            orgname numeric (10) not null,
            inactive varchar (1) not null default '0',
	    send_at_time numeric(4),
