	line_seq_nbr decimal (10) not null,  --  sequence number within voucher
	fin_account decimal (10) not null,  -- the financial account that this voucher detail applies to
	debit_amount decimal (20,3),  -- the debit amount, if this account is to be debited
	credit_amount decimal (20,3),  -- the credit amount, if this account is to be credited
	description varchar (1000) not null,  -- narration for the voucher detail
	guid varchar (200) not null , -- postgres: default uuid_generate_v4(),
	orgname decimal (10) not null,
	inactive char (1) default '0',
	created_by decimal (10) not null,
	modified_by decimal (10),
	created_date timestamp default current_timestamp,
	modified_date timestamp,
  constraint fin_voucher_detail__pk primary key (fin_voucher_detail_id));
alter table fin_voucher_detail add constraint fk__fin_v_det_acct foreign key (fin_account) references fin_account (fin_account_id);
alter table fin_voucher_detail add constraint fk__fin_v_det_hdr_crt_by foreign key (created_by) references sbeuser (user_id);
alter table fin_voucher_detail add constraint fk__fin_v_det_hdr_mod_by foreign key (modified_by) references sbeuser (user_id);
alter table fin_voucher_detail add constraint fk__fin_v_det_orgname foreign key (orgname) references orgname (orgname_id);

